`include "Definition.h"

module vga(
	clk_25M,rst,hs,vs,red,green,blue,is_snake,is_apple,is_score,is_border,is_attention,is_meditation,is_signal,x_pos,y_pos,game_status
    );
	 
	 input clk_25M,rst;
    input game_status;                           //  输入游戏状态信息：持续 reg 信号
	 output reg hs,vs;							  //  行，列消隐信号
	 input is_signal;
	 output reg [3:0] red,green,blue;	          //  输出颜色信号
	 input is_snake,is_apple;					  //  输入蛇身和苹果确认信息
	 input is_score,is_border,is_attention,is_meditation;
	 output reg [6:0] x_pos;				      //  输出x的坐标信息，最大为 640/8 = 80
	 output reg [5:0] y_pos;				      //  输出y的坐标信息,最大为 480/8 = 60
	 reg [9:0] x_pix,y_pix;
	 reg [9:0] x,y;
	 reg [9:0] x1,y1;
	 reg [9:0] x2,y2;
	 reg [9:0] x3,y3;
	 reg [9:0] x4,y4;

//	行消隐信号，场消隐信号产生模块
//	640 * 480 的像素为 800 * 521
	reg [9:0] hcnt = 10'd0;		//	行像素计数
	reg [9:0] vcnt = 10'd0;		//	列的行计数
//	reg [9:0] x,y;					//	输出绝对坐标信息
	parameter [9:0] 
		hcnt_max = 10'd799,
		vcnt_max = 10'd520;
	always @(posedge clk_25M or negedge rst)
	begin
		if(!rst)
			begin
			hcnt <= 10'd0;
			vcnt <= 10'd0;
			end
		else
			if(hcnt < hcnt_max)
				hcnt <= hcnt + 1;
			else
				begin
					hcnt <= 10'd0;
					if(vcnt < vcnt_max)
						vcnt <= vcnt + 1;
					else
						vcnt <= 10'd0;
				end
	end
//	行，列同步信号的产生
//	显示后沿与显示前沿都输出的是高电平信号
	always @ (posedge clk_25M)
	begin
		if(hcnt > 95)
			hs <= 1'b1;
		else
			hs <= 1'b0;
	end
	always @ (posedge clk_25M)
	begin
		if(vcnt > 1)
			vs <= 1'b1;
		else 
			vs <= 1'b0;
	end
//	输出扫描的坐标信息
//	6/23 : 修改VGA显示时序  Active Front Sync Back
//								H:	640	  16	  96   48	800
//								V:	480	  10     2   33	525
	always @ (posedge clk_25M)
	begin
		if ((hcnt > 144) && ( hcnt < 785 ) && (vcnt > 34) && (vcnt < 515))	
			begin
				x_pos <= (hcnt - 145)>>3;
				y_pos <= (vcnt - 35)>>3;
				x_pix <= hcnt - 145;
				y_pix <= vcnt - 35;
			end
		else
			begin
				x_pos <= 0;
				y_pos <= 0;
			end						
	end
//	绘制蛇身与苹果
//	蛇身为红色
//	苹果为绿色
	always @ (posedge clk_25M or negedge rst)
	begin
		if(!rst)
			{red,green,blue} <= 12'b0000_0000_0000;
		else
         if(game_status == `Start)
			begin
			if(is_snake)											//	红色蛇
				{red,green,blue} <= 12'b1111_0000_0000;
			else if(is_apple)										//	绿色苹果
				{red,green,blue} <= 12'b0000_1111_0000;
			else if(is_score)										//	蓝色分数
				{red,green,blue} <= 12'b0000_0000_1111;
			else if(is_border)									//	黄色边界
				{red,green,blue} <= 12'b1111_1111_0000;
			else if(is_attention)
				{red,green,blue} <= 12'b0000_0000_1111;
			else if(is_meditation)
				{red,green,blue} <= 12'b0000_0000_1111;
			else if(is_signal)
				{red,green,blue} <= 12'b0000_0000_1111;
			else
